function cambiarChecks(){checkButtons=$('input[type="checkbox"]');checkButtons.each(function(){var e=this.checked?"checked":"";$(this).after('<span rel="'+this.id+'" class="footage-checkbox '+e+'"></span>').css("display","none")});checkButtons.change(function(){this.checked=this.checked?false:true;if(this.checked)$('span.footage-checkbox[rel="'+this.id+'"]').addClass("checked");else $('span.footage-checkbox[rel="'+this.id+'"]').removeClass("checked")});$("span.footage-checkbox").click(function(){tempChk=$("input#"+$(this).attr("rel")).change()})}function cambiarRadios(){radioButtons=$('input[type="radio"]');radioButtons.each(function(){$(this).after('<span rel="'+this.id+'" class="footage-radio"></span>').css("display","none")});$('input[type="radio"]:checked').each(function(){$('span.footage-radio[rel="'+this.id+'"]').addClass("checked")});radioButtons.change(function(){radioButtons.each(function(){$('span.footage-radio[rel="'+this.id+'"]').removeClass("checked");this.checked=false});$('span.footage-radio[rel="'+this.id+'"]').addClass("checked");this.checked=true});$("span.footage-radio").click(function(){tempRadio=$("input#"+$(this).attr("rel")).change()})}$(document).ready(function(){cambiarChecks()})